$(function() {
  return $('#shop_output_item_name,#shop_input_item_name').typeahead({
    source: function(query, process) {
      return $.ajax({
        url: '/items',
        dataType: 'json',
        data: {
          term: query
        },
        success: function(data) {
          return process($.map(data, function(item) {
            return item.name;
          }));
        }
      });
    }
  });
});

